import React from 'react';
import styled from 'styled-components';
import avatar from '../../img/hoshiro.png';
import { signout } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';
import { useDarkMode } from '../Dark/DarkModeContext';

function Navigation({ active, setActive }) {
  const { darkMode, toggleDarkMode } = useDarkMode();

  return (
    <NavStyled darkMode={darkMode}>
      <div className="user-con">
        <img src={avatar} alt="" />
        <div className="text">
          <h2>Harris</h2>
          <p>My Savings</p>
        </div>
      </div>
      <ul className="menu-items">
        {menuItems.map((item) => (
          <li
            key={item.id}
            onClick={() => setActive(item.id)}
            className={active === item.id ? 'active' : ''}
          >
            {item.icon}
            <span>{item.title}</span>
          </li>
        ))}
      </ul>
      <div className="bottom-nav">
        <li>
          {signout} Sign Out
        </li>
      </div>
      <button className="dark-mode-toggle" onClick={toggleDarkMode}>
        {darkMode ? 'Light Mode' : 'Dark Mode'}
      </button>
    </NavStyled>
  );
}

const NavStyled = styled.nav`
  padding: 2rem 1.5rem;
  width: 374px;
  height: 100%;
  background: ${({ darkMode }) => (darkMode ? '#333' : 'rgba(252, 246, 249, 0.78)')};
  border: 3px solid ${({ darkMode }) => (darkMode ? '#444' : '#FFFFFF')};
  backdrop-filter: blur(4.5px);
  border-radius: 32px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  gap: 2rem;

  .user-con {
    height: 100px;
    display: flex;
    align-items: center;
    gap: 1rem;
    img {
      width: 80px;
      height: 80px;
      border-radius: 50%;
      object-fit: cover;
      background: ${({ darkMode }) => (darkMode ? '#444' : '#fcf6f9')};
      border: 2px solid ${({ darkMode }) => (darkMode ? '#555' : '#FFFFFF')};
      padding: .2rem;
      box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
    }
    h2 {
      color: ${({ darkMode }) => (darkMode ? '#FFF' : 'rgba(34, 34, 96, 1)')};
    }
    p {
      color: ${({ darkMode }) => (darkMode ? '#CCC' : 'rgba(34, 34, 96, .6)')};
    }
  }

  .menu-items {
    flex: 1;
    display: flex;
    flex-direction: column;
    li {
      display: grid;
      grid-template-columns: 40px auto;
      align-items: center;
      margin: .6rem 0;
      font-weight: 500;
      cursor: pointer;
      transition: all .4s ease-in-out;
      color: ${({ darkMode }) => (darkMode ? '#CCC' : 'rgba(34, 34, 96, .6)')};
      padding-left: 1rem;
      position: relative;
      i {
        color: ${({ darkMode }) => (darkMode ? '#CCC' : 'rgba(34, 34, 96, 0.6)')};
        font-size: 1.4rem;
        transition: all .4s ease-in-out;
      }
    }
  }

  .active {
    color: ${({ darkMode }) => (darkMode ? '#FFF' : 'rgba(34, 34, 96, 1)')} !important;
    i {
      color: ${({ darkMode }) => (darkMode ? '#FFF' : 'rgba(34, 34, 96, 1)')} !important;
    }
    &::before {
      content: "";
      position: absolute;
      left: 0;
      top: 0;
      width: 4px;
      height: 100%;
      background: ${({ darkMode }) => (darkMode ? '#FFF' : '#222260')};
      border-radius: 0 10px 10px 0;
    }
  }

  .bottom-nav {
    li {
      color: ${({ darkMode }) => (darkMode ? '#CCC' : 'rgba(34, 34, 96, .6)')};
      i {
        color: ${({ darkMode }) => (darkMode ? '#CCC' : 'rgba(34, 34, 96, .6)')};
      }
    }
  }

  .dark-mode-toggle {
    padding: 0.5rem 1rem;
    border: none;
    border-radius: 5px;
    background: ${({ darkMode }) => (darkMode ? '#FFF' : '#222260')};
    color: ${({ darkMode }) => (darkMode ? '#222260' : '#FFF')};
    cursor: pointer;
    transition: all 0.3s ease-in-out;
  }
`;

export default Navigation;
