import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { dollar, moon, sun } from '../../utils/Icons'; // Assuming moon and sun icons are imported
import Chart from '../Chart/Chart';

function Dashboard() {
    const { totalExpenses, incomes, expenses, totalIncome, totalBalance, getIncomes, getExpenses, darkMode, toggleDarkMode } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, []);

    return (
        <DashboardStyled darkMode={darkMode}>
            <InnerLayout>
                <h1>All Transactions</h1>
                <div className="stats-con">
                    <div className="chart-con">
                        <Chart />
                        <div className="amount-con">
                            <div className="income">
                                <h2>Total Income</h2>
                                <p>
                                    {dollar} {totalIncome()}
                                </p>
                            </div>
                            <div className="expense">
                                <h2>Total Expense</h2>
                                <p>
                                    {dollar} {totalExpenses()}
                                </p>
                            </div>
                            <div className="balance">
                                <h2>Total Balance</h2>
                                <p>
                                    {dollar} {totalBalance()}
                                </p>
                            </div>
                        </div>
                    </div>
                    <div className="history-con">
                        <History />
                        <h2 className="salary-title">Min <span>Salary</span>Max</h2>
                        <div className="salary-item">
                            <p>
                                ₹{Math.min(...incomes.map(item => item.amount))}
                            </p>
                            <p>
                                ₹{Math.max(...incomes.map(item => item.amount))}
                            </p>
                        </div>
                        <h2 className="salary-title">Min <span>Expense</span>Max</h2>
                        <div className="salary-item">
                            <p>
                                ₹{Math.min(...expenses.map(item => item.amount))}
                            </p>
                            <p>
                                ₹{Math.max(...expenses.map(item => item.amount))}
                            </p>
                        </div>
                    </div>
                </div>
            </InnerLayout>
            <DarkModeToggle onClick={toggleDarkMode}>
                {darkMode ? <span>{sun}</span> : <span>{moon}</span>}
                {darkMode ? 'Light Mode' : 'Dark Mode'}
            </DarkModeToggle>
        </DashboardStyled>
    );
}

const DashboardStyled = styled.div`
    background: ${({ darkMode }) => (darkMode ? '#333' : 'rgba(252, 246, 249, 0.78)')};
    color: ${({ darkMode }) => (darkMode ? '#fff' : 'inherit')};

    .stats-con {
        display: grid;
        grid-template-columns: repeat(5, 1fr);
        gap: 2rem;

        .chart-con {
            grid-column: 1 / 4;
            height: 400px;
            .amount-con {
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                gap: 2rem;
                margin-top: 2rem;

                .income,
                .expense,
                .balance {
                    background: ${({ darkMode }) => (darkMode ? '#444' : '#FCF6F9')};
                    border: 2px solid ${({ darkMode }) => (darkMode ? '#555' : '#FFFFFF')};
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;
                    p {
                        font-size: 3.5rem;
                        font-weight: 700;
                    }
                }

                .balance {
                    grid-column: 2 / 4;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    p {
                        color: var(--color-green);
                        opacity: 0.6;
                        font-size: 4.5rem;
                    }
                }
            }
        }

        .history-con {
            grid-column: 4 / -1;
            h2 {
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }

            .salary-title {
                font-size: 1.2rem;
                span {
                    font-size: 1.8rem;
                }
            }

            .salary-item {
                background: ${({ darkMode }) => (darkMode ? '#444' : '#FCF6F9')};
                border: 2px solid ${({ darkMode }) => (darkMode ? '#555' : '#FFFFFF')};
                box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                padding: 1rem;
                border-radius: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                p {
                    font-weight: 600;
                    font-size: 1.6rem;
                }
            }
        }
    }
`;

const DarkModeToggle = styled.button`
    position: fixed;
    bottom: 20px;
    right: 20px;
    background: ${({ darkMode }) => (darkMode ? '#FFF' : '#222260')};
    color: ${({ darkMode }) => (darkMode ? '#222260' : '#FFF')};
    border: none;
    border-radius: 50%;
    width: 50px;
    height: 50px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.5rem;
    cursor: pointer;
`;

export default Dashboard;
